        <Statistics
          good={this.state.good }
          neutral={this.state.neutral }
          bad={this.state.bad }
          total={this.countTotalFeedback }
            positivePercentage={this.countPositiveFeedbackPercentage} />
          </Section>
      </div>
    )
  }
}

export default App;
